import React, { createContext, useState, useContext, useEffect, ReactNode } from 'react';
import { toast } from 'react-toastify';
import axios from 'axios';

interface User {
  id: string;
  name: string;
  email: string;
  credits: number;
  apiKey: string;
  apiUrl: string;
  isRecharged: boolean;
}

interface AuthContextType {
  user: User | null;
  loading: boolean;
  error: string | null;
  isAuthenticated: boolean;
  login: (token: string) => void;
  logout: () => void;
  refreshUser: () => void;
  refreshCredits: () => void;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

interface AuthProviderProps {
  children: ReactNode;
}

export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    // Check if user is logged in from localStorage
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');
    
    if (token && userData) {
      try {
        setUser(JSON.parse(userData));
      } catch (error) {
        console.error('Failed to parse user data:', error);
        localStorage.removeItem('token');
        localStorage.removeItem('user');
      }
    }
    
    setLoading(false);
  }, []);

  // Display API credentials in toast notifications instead of trying to send an email
  const sendCredentialsEmail = async (userEmail: string, apiKey: string, apiUrl: string, userName: string) => {
    try {
      // Log credentials for development purposes
      console.log("----------- YOUR API CREDENTIALS -----------");
      console.log(`Email: ${userEmail}`);
      console.log(`API URL: ${apiUrl}`);
      console.log(`API Key: ${apiKey}`);
      console.log(`Initial Credits: 4`);
      console.log("-------------------------------------------");
      
      // Show credentials in toast notifications
      toast.success(`📝 Welcome ${userName}! Your account has been created.`);
      
      // Use setTimeout to space out the notifications
      setTimeout(() => {
        toast.info(`📌 API URL: ${apiUrl}`, {
          autoClose: 10000 // stay open for 10 seconds
        });
      }, 1000);
      
      setTimeout(() => {
        toast.info(`🔑 API Key: ${apiKey}`, {
          autoClose: 10000 // stay open for 10 seconds
        });
      }, 2000);
      
      setTimeout(() => {
        toast.info(`💳 Initial Credits: 4`, {
          autoClose: 10000 // stay open for 10 seconds
        });
      }, 3000);
      
      setTimeout(() => {
        toast.info(`⚠️ Please save these credentials! You can always find them on your dashboard.`, {
          autoClose: 10000 // stay open for 10 seconds
        });
      }, 4000);
      
      // Try to copy API key to clipboard
      try {
        await navigator.clipboard.writeText(apiKey);
        setTimeout(() => {
          toast.success(`API Key copied to clipboard!`);
        }, 5000);
      } catch (err) {
        console.error('Could not copy to clipboard', err);
      }
    } catch (error) {
      console.error('Failed to process credentials:', error);
      toast.error('Failed to process credentials. Please check your dashboard for API details.');
    }
  };

  const login = (token: string) => {
    try {
      // Decode the Google JWT token to get user info
      // JWT tokens are in format: header.payload.signature
      const payload = token.split('.')[1];
      const decodedPayload = JSON.parse(atob(payload));
      
      // Generate a unique API key
      const uniqueApiKey = `key_${Math.random().toString(36).substring(2, 15)}_${Math.random().toString(36).substring(2, 15)}`;
      const apiUrl = `${window.location.origin}/api`;
      
      // Extract user information from the token
      const userData: User = {
        id: decodedPayload.sub,
        name: decodedPayload.name || decodedPayload.email.split('@')[0],
        email: decodedPayload.email,
        credits: 4, // Start with 4 credits
        apiKey: uniqueApiKey,
        apiUrl: apiUrl,
        isRecharged: false
      };

      // Save user data
      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(userData));
      setUser(userData);
      
      // Send credentials email automatically
      sendCredentialsEmail(userData.email, uniqueApiKey, apiUrl, userData.name);
      
      toast.success('Logged in successfully!');
    } catch (error) {
      console.error('Error parsing JWT token:', error);
      // Fallback to mock data if token parsing fails
      const mockUserData: User = {
        id: '123456',
        name: 'Test User',
        email: 'test@example.com',
        credits: 4,
        apiKey: 'sample-api-key-for-testing',
        apiUrl: `${window.location.origin}/api`,
        isRecharged: false
      };

      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(mockUserData));
      setUser(mockUserData);
      toast.success('Logged in successfully!');
      
      // Still show credentials for mock user
      sendCredentialsEmail(mockUserData.email, mockUserData.apiKey, mockUserData.apiUrl, mockUserData.name);
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    toast.info('Logged out successfully');
    
    // Force navigate to home page
    window.location.href = '/';
  };

  const refreshUser = () => {
    // Mock implementation - in a real app this would fetch updated user data
    if (user) {
      const updatedUser = { ...user };
      setUser(updatedUser);
    }
  };

  const refreshCredits = () => {
    // Mock implementation - in a real app this would refresh credits count
    if (user) {
      const updatedUser = { ...user, credits: 100 };
      localStorage.setItem('user', JSON.stringify(updatedUser));
      setUser(updatedUser);
      toast.success('Credits refreshed successfully!');
    }
  };

  return (
    <AuthContext.Provider value={{ 
      user, 
      loading,
      error,
      isAuthenticated: !!user, 
      login, 
      logout,
      refreshUser,
      refreshCredits
    }}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
