import React, { useState, useRef } from 'react';
import { Modal, Button, Form, Alert } from 'react-bootstrap';
import { useAuth } from '../context/AuthContext';
import { useReactToPrint } from 'react-to-print';

interface CredentialsModalProps {
  show: boolean;
  onHide: () => void;
}

const CredentialsModal: React.FC<CredentialsModalProps> = ({ show, onHide }) => {
  const { user, acknowledgeNewCredentials } = useAuth();
  const [copied, setCopied] = useState<string | null>(null);
  const printRef = useRef<HTMLDivElement>(null);
  
  const handlePrint = useReactToPrint({
    content: () => printRef.current,
    documentTitle: 'API_Credentials',
    onAfterPrint: () => console.log('Printed credentials successfully')
  });

  if (!user) {
    return null;
  }

  const copyToClipboard = (text: string, field: string) => {
    navigator.clipboard.writeText(text);
    setCopied(field);
    setTimeout(() => setCopied(null), 2000);
  };

  const handleClose = () => {
    acknowledgeNewCredentials();
    onHide();
  };
  
  // Format current date for email subject
  const formattedDate = new Date().toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'short',
    day: 'numeric'
  });
  
  // Create mailto link with pre-filled credentials
  const mailtoLink = `mailto:${user.email}?subject=Your CRUD API Credentials (${formattedDate})&body=Dear ${user.name},%0A%0AHere are your CRUD API credentials:%0A%0AAPI URL: ${user.apiUrl}%0AAPI Key: ${user.apiKey}%0AInitial Credits: ${user.credits}%0A%0AThank you for using our service!%0A%0ARegards,%0ACRUD API Platform Team`;

  return (
    <Modal 
      show={show} 
      onHide={handleClose}
      size="lg"
      centered
      backdrop="static"
    >
      <Modal.Header closeButton>
        <Modal.Title>Your API Credentials</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Alert variant="info">
          <Alert.Heading>Important - Save these credentials!</Alert.Heading>
          <p>
            These are your unique API credentials to use with the CRUD API Platform. 
            Please save these details as they will be needed to authenticate your API requests.
          </p>
        </Alert>

        <div ref={printRef}>
          <div className="p-3 border rounded mb-4">
            <h4 className="text-center mb-3">CRUD API Platform Credentials</h4>
            <p><strong>User:</strong> {user.name} ({user.email})</p>
            <p><strong>Date Issued:</strong> {new Date().toLocaleDateString()}</p>
            <hr />
            
            <Form>
              <Form.Group className="mb-3">
                <Form.Label>Your API URL</Form.Label>
                <div className="d-flex">
                  <Form.Control 
                    type="text" 
                    value={user.apiUrl} 
                    readOnly 
                    onClick={(e) => (e.target as HTMLInputElement).select()}
                  />
                  <Button 
                    variant="outline-secondary" 
                    className="ms-2 d-print-none" 
                    onClick={() => copyToClipboard(user.apiUrl, 'url')}
                  >
                    {copied === 'url' ? 'Copied!' : 'Copy'}
                  </Button>
                </div>
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Your API Key</Form.Label>
                <div className="d-flex">
                  <Form.Control 
                    type="text" 
                    value={user.apiKey} 
                    readOnly 
                    onClick={(e) => (e.target as HTMLInputElement).select()}
                  />
                  <Button 
                    variant="outline-secondary" 
                    className="ms-2 d-print-none" 
                    onClick={() => copyToClipboard(user.apiKey, 'key')}
                  >
                    {copied === 'key' ? 'Copied!' : 'Copy'}
                  </Button>
                </div>
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Initial Credits</Form.Label>
                <Form.Control 
                  type="text" 
                  value={user.credits.toString()} 
                  readOnly 
                />
              </Form.Group>
            </Form>

            <hr />
            <h5>How to use these credentials</h5>
            <p>In the Todo App, you'll need to enter these credentials in the configuration screen.</p>
            <p>If you're using the library directly in your own code:</p>
            <pre className="bg-light p-3 rounded">
{`// Method 1: Set environment variables
CRUD_API_URL=${user.apiUrl}
CRUD_API_KEY=${user.apiKey}

// Method 2: Set programmatically
Crublibrary.setCredentials('${user.apiUrl}', '${user.apiKey}');`}
            </pre>
          </div>
        </div>
        
        <div className="d-flex gap-2 mb-2 d-print-none">
          <Button 
            variant="primary" 
            className="w-100"
            onClick={handlePrint}
          >
            <i className="bi bi-printer"></i> Print Credentials
          </Button>
          
          <Button 
            variant="outline-primary" 
            className="w-100"
            href={mailtoLink}
            target="_blank"
          >
            <i className="bi bi-envelope"></i> Email to Myself
          </Button>
        </div>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          I've saved my credentials
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default CredentialsModal;
