import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Card, Container, Row, Col, Button } from 'react-bootstrap';
import { useAuth } from '../context/AuthContext';
import { CLIENT_ID } from '../config/google-auth';

// Declare global google type
declare global {
  interface Window {
    google: any;
  }
}

const Login: React.FC = () => {
  const { isAuthenticated, login } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    if (isAuthenticated) {
      navigate('/dashboard');
    }
  }, [isAuthenticated, navigate]);

  useEffect(() => {
    // Load Google Sign-In API script
    const script = document.createElement('script');
    script.src = 'https://accounts.google.com/gsi/client';
    script.async = true;
    script.defer = true;
    document.body.appendChild(script);

    script.onload = () => {
      // Initialize Google Sign-In
      window.google.accounts.id.initialize({
        client_id: CLIENT_ID,
        callback: handleGoogleResponse,
      });
      
      // Render Google Sign-In button
      window.google.accounts.id.renderButton(
        document.getElementById('google-signin'),
        { 
          theme: 'outline', 
          size: 'large',
          text: 'signin_with',
          shape: 'rectangular',
          logo_alignment: 'center',
          width: 250
        }
      );
    };

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  const handleGoogleResponse = (response: any) => {
    // Process Google credential response
    const token = response.credential;
    if (token) {
      login(token);
    }
  };

  return (
    <Container className="mt-5">
      <Row className="justify-content-md-center">
        <Col md={6}>
          <Card>
            <Card.Body className="p-4">
              <div className="text-center mb-4">
                <h2>Login to FormPilot CRUD</h2>
                <p className="text-muted">Access your API management dashboard</p>
              </div>
              
              <div className="d-flex justify-content-center mb-4">
                <div id="google-signin"></div>
              </div>
              
              <div className="mt-4 pt-3 border-top">
                <h5>What you'll get:</h5>
                <ul className="mt-3">
                  <li className="mb-2">Unique API credentials for CRUD operations</li>
                  <li className="mb-2">Personal dashboard to manage your API usage</li>
                  <li className="mb-2">4 free API credits to get started</li>
                  <li className="mb-2">Email delivery of your API credentials</li>
                </ul>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Login;
