import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Alert } from 'react-bootstrap';
import { ToastContainer, toast } from 'react-toastify';
import TodoForm from './components/TodoForm';
import TodoList from './components/TodoList';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'react-toastify/dist/ReactToastify.css';

// Import the Crublibrary - this is linked from our local package
const Crublibrary = require('your-formpilot-crud');

// Configure API credentials
// In production, these would be set in .env file
if (!process.env.REACT_APP_CRUD_API_URL || !process.env.REACT_APP_CRUD_API_KEY) {
  console.warn('API credentials not set. Please set REACT_APP_CRUD_API_URL and REACT_APP_CRUD_API_KEY in .env file');
}

// Set environment variables for the library
process.env.CRUD_API_URL = process.env.REACT_APP_CRUD_API_URL;
process.env.CRUD_API_KEY = process.env.REACT_APP_CRUD_API_KEY;

interface Todo {
  id: string;
  value: number;
  txHash: string;
}

const App: React.FC = () => {
  const [todos, setTodos] = useState<Todo[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [creditsExhausted, setCreditsExhausted] = useState<boolean>(false);

  // Load todos on component mount
  useEffect(() => {
    const fetchTodos = async () => {
      try {
        // Instead of loading all todos at once, we'll initialize with an empty list
        // This is because the CRUD API doesn't support listing all items
        setTodos([]);
        setLoading(false);
      } catch (err: any) {
        handleApiError(err);
        setLoading(false);
      }
    };

    fetchTodos();
  }, []);

  // Handle API errors
  const handleApiError = (err: any) => {
    console.error('API Error:', err);
    const errorMessage = err.message || 'An unknown error occurred';
    
    // Check if the error is due to exhausted credits
    if (errorMessage.includes('Request limit exceeded')) {
      setCreditsExhausted(true);
      toast.error('Credits exhausted. Please recharge by mailing support.');
    } else {
      toast.error(errorMessage);
    }
    
    setError(errorMessage);
  };

  // Create a new todo
  const createTodo = async (value: number, txHash: string) => {
    try {
      setError(null);
      const response = await Crublibrary.create({ value, txHash });
      const newTodo = { id: response.id, value, txHash };
      setTodos([...todos, newTodo]);
      toast.success('Todo created successfully!');
    } catch (err: any) {
      handleApiError(err);
    }
  };

  // Get a todo by ID
  const getTodo = async (id: string) => {
    try {
      setError(null);
      const todo = await Crublibrary.get(id);
      return todo;
    } catch (err: any) {
      handleApiError(err);
      return null;
    }
  };

  // Update a todo
  const updateTodo = async (id: string, value: number, txHash: string) => {
    try {
      setError(null);
      await Crublibrary.update(id, { value, txHash });
      setTodos(todos.map(todo => 
        todo.id === id ? { ...todo, value, txHash } : todo
      ));
      toast.success('Todo updated successfully!');
    } catch (err: any) {
      handleApiError(err);
    }
  };

  // Delete a todo
  const deleteTodo = async (id: string) => {
    try {
      setError(null);
      await Crublibrary.delete(id);
      setTodos(todos.filter(todo => todo.id !== id));
      toast.success('Todo deleted successfully!');
    } catch (err: any) {
      handleApiError(err);
    }
  };

  return (
    <div className="App">
      <Container className="mt-5">
        <Row className="justify-content-center">
          <Col md={8}>
            <h1 className="text-center mb-4">Todo App</h1>
            
            {creditsExhausted && (
              <Alert variant="warning" className="mb-4">
                <Alert.Heading>Credits Exhausted</Alert.Heading>
                <p>
                  Your API credits have been exhausted. Please recharge by mailing support at: <strong>support@yourapp.com</strong>
                </p>
                <p className="mb-0">
                  Email subject should be: <strong>Please recharge my credits</strong>
                </p>
              </Alert>
            )}

            <Card className="shadow-sm mb-4">
              <Card.Body>
                <Card.Title>Add New Todo</Card.Title>
                <TodoForm onSubmit={createTodo} disabled={creditsExhausted} />
              </Card.Body>
            </Card>

            <Card className="shadow-sm">
              <Card.Body>
                <Card.Title>Your Todos</Card.Title>
                {loading ? (
                  <p className="text-center">Loading...</p>
                ) : (
                  <TodoList 
                    todos={todos} 
                    onUpdate={updateTodo}
                    onDelete={deleteTodo}
                    disabled={creditsExhausted}
                  />
                )}
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
      <ToastContainer position="bottom-right" />
    </div>
  );
};

export default App;
